const numberOfFields = $input.first().json['Number of Backlinks Per Post'];
const clientWebsite = $input.first().json['Client Website URL (this is the website where the blog post with our backlinks are posted)'];
const formFields = [];

// Create form fields for backlinks
for (let i = 1; i <= numberOfFields; i++) {
  formFields.push({
    fieldLabel: 'Back link URL' + i,
    placeholder: 'www.oursite.com',
    fieldType: 'text',
    requiredField: true
  });
}

// Prepare client website for initial scraping
const urlsToScrape = [{
  url: clientWebsite,
  type: 'client'
}];

return [{ 
  json: { 
    nextform: formFields,
    clientWebsite: clientWebsite,
    urlsToScrape: urlsToScrape
  } 
}];

const numberOfPosts = $('On form submission').first().json['Number of Blog Posts to be generated'];
const contentFields = [];

// Create form fields for blog post content/topics
for (let i = 1; i <= numberOfPosts; i++) {
  contentFields.push({
    fieldLabel: `Blog Post ${i} Topic/Content Idea`,
    placeholder: `Enter the topic or content idea for blog post ${i}`,
    fieldType: 'text',
    requiredField: true
  });
}

return [{ 
  json: { 
    contentform: contentFields
  } 
}];

const clientWebsite = $('On form submission').first().json['Client Website URL (this is the website where the blog post with our backlinks are posted)'];
const numberOfBacklinks = $('On form submission').first().json['Number of Backlinks Per Post'];

// Collect all backlink URLs from the form
const backlinkUrls = [];
for (let i = 1; i <= numberOfBacklinks; i++) {
  const backlinkUrl = $('Form').first().json[`Back link URL${i}`];
  if (backlinkUrl && backlinkUrl.trim() !== '') {
    // Ensure URL has protocol
    let formattedUrl = backlinkUrl.trim();
    if (!formattedUrl.startsWith('http://') && !formattedUrl.startsWith('https://')) {
      formattedUrl = 'https://' + formattedUrl;
    }
    backlinkUrls.push(formattedUrl);
  }
}

// Format client website URL
let clientUrl = clientWebsite.trim();
if (!clientUrl.startsWith('http://') && !clientUrl.startsWith('https://')) {
  clientUrl = 'https://' + clientUrl;
}

// Combine all URLs to scrape
const allUrls = [
  { url: clientUrl, type: 'client' },
  ...backlinkUrls.map(url => ({ url: url, type: 'backlink' }))
];

// Return each URL as separate item for parallel processing
return allUrls.map(item => ({ 
  json: { 
    url: item.url, 
    type: item.type,
    projectName: $('On form submission').first().json['Project Name']
  } 
}));

const url = $json.url;
const type = $json.type;
const projectName = $json.projectName;
const htmlContent = $json.data || '';

try {
  // Extract text content (basic HTML parsing)
  let textContent = htmlContent
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '') // Remove scripts
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '') // Remove styles
    .replace(/<[^>]*>/g, ' ') // Remove HTML tags
    .replace(/\s+/g, ' ') // Replace multiple whitespace with single space
    .trim();

  // Limit content length (first 3000 characters for context)
  textContent = textContent.substring(0, 3000);

  // Extract title
  const titleMatch = htmlContent.match(/<title[^>]*>([\s\S]*?)<\/title>/i);
  const title = titleMatch ? titleMatch[1].trim().replace(/\s+/g, ' ') : 'No title found';

  // Extract meta description
  const metaMatch = htmlContent.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']*)["']/i);
  const description = metaMatch ? metaMatch[1] : 'No description found';

  // Extract keywords if available
  const keywordsMatch = htmlContent.match(/<meta[^>]*name=["']keywords["'][^>]*content=["']([^"']*)["']/i);
  const keywords = keywordsMatch ? keywordsMatch[1] : '';

  // Extract H1 tags for main topics
  const h1Matches = htmlContent.match(/<h1[^>]*>([\s\S]*?)<\/h1>/gi) || [];
  const mainTopics = h1Matches.map(h1 => h1.replace(/<[^>]*>/g, '').trim()).slice(0, 5);

  return { webcontent : {
    json: {
      url: url,
      type: type,
      projectName: projectName,
      title: title,
      description: description,
      keywords: keywords,
      mainTopics: mainTopics,
      content: textContent,
      contentLength: textContent.length,
      scrapedAt: new Date().toISOString(),
      status: 'success'
    }
  }
         };
} catch (error) {
  return {
    json: {
      url: url,
      type: type,
      projectName: projectName,
      title: 'Error scraping',
      description: 'Failed to scrape content',
      keywords: '',
      mainTopics: [],
      content: 'Content unavailable due to scraping error',
      contentLength: 0,
      scrapedAt: new Date().toISOString(),
      status: 'error',
      error: error.message
    }
  };
}

// Transform the input data array and prepare blog content ideas
const inputData = $input.first().json.data;
const numberOfPosts = $('On form submission').first().json['Number of Blog Posts to be generated'];

// Collect blog content ideas from the content form
const blogContentIdeas = [];
for (let i = 1; i <= numberOfPosts; i++) {
  const contentIdea = $('Content Form').first().json[`Blog Post ${i} Topic/Content Idea`];
  if (contentIdea && contentIdea.trim() !== '') {
    blogContentIdeas.push(contentIdea.trim());
  }
}

// Transform the data
const transformedData = {
  client_website_information: inputData[0].output,
  backlink_website_information: inputData.slice(1).map(item => item.output),
  blog_content_ideas: blogContentIdeas
};

// Output the transformed data
console.log(JSON.stringify(transformedData, null, 2));
